INSERT INTO `noticia` (`id`, `titulo_pt`, `titulo_en`, `noticia_pt`, `noticia_en`, `criacao`, `publicacao`, `img`, `online`) VALUES
(1, 'Filda 2016', 'Filda 2016', 'Pelo terceiro ano consecutivo, estaremos presentes na Feira Internacional de Luanda. Um evento que nos permite mais um momento de proximidade aos nossos clientes Angolanos. De 19 a 24 de Julho de 2016.', 'For the third consecutive year, we will be attending the Luanda International Fair. An event that allows us a moment of closeness with our Angolan clients. From 19 to 24 July 2016.', '2016-02-12', '2016-02-12', '/img/noticias/001.jpg', 1),
(2, 'A Claus chegou ao showroom', 'Claus has arrived at the showroom', 'Na Ci estamos sempre há procura de produtos diferenciadores. Desta vez a aposta foi para a marca de fragrâncias Claus Porto. Além de ser uma experiência única para os nossos sentidos, a imagem cuidada com que os produtos se apresentam torna-os em elementos decorativos marcantes.', 'At Ci we\'re always looking for differentiating products. This time the bet was on the fragrance brand Claus Porto. Besides being a unique experience for our senses, the careful image with which the products present themselves makes them into striking decorative elements.', '2016-02-18', '2016-02-18', '/img/noticias/002.jpg', 1),
(3, 'Open Day |Próximo Dia 02 de Julho', 'Open Day |Next July 2nd', 'Numa iniciativa conjunta com a Offa Arquitectura, no próximo dia 02 de Julho estaremos de portas abertas para vos dar a conhecer os diversos trabalhos deste Atelier, num espaço da sua criação para a Ci Interior Decor. A Arquitectura e o Design de Interiores esperam por si, numa tarde de Sábado que irá certamente valer a pena.', 'In a joint initiative with Offa Arquitectura, on the next July 2nd we will be open to present the various works of this Atelier, at a space of their creation for Ci Interior Decor. Architecture and Interior Design are waiting for you, on a Saturday afternoon that will surely be worth it.', '2016-06-11', '2016-06-11', '/img/noticias/0003.jpg', 1),
(4, 'A Ci no Porto', 'Ci in Oporto', 'Em breve teremos um novo Showroom! \r\nPretendemos levar mais longe a marca Ci e elegemos a encantadora cidade do Porto para abrir um segundo espaço ao nível nacional. Brevemente as peças de nosso design, as conceituadas marcas que representamos e todos os nossos serviços estarão ainda mais perto de si.', 'Soon we will have a new Showroom!\r\nWe intend to expand the CI brand and we have chosen the lovely city of Oporto to open a second space nationwide. Soon our design pieces, the well-known brands that we represent and all our services will be even closer to you.', '2016-07-06', '2016-07-06', '/img/noticias/0004.jpg', 1),
(5, '', '', '', '', '2016-08-26', '2016-08-26', '/img/noticias/0005.jpg', 1),
(6, 'Participação na Index Dubai Design Series 2017', 'Participation on the Index Dubai Design Series 2017', 'Estaremos presentes no Dubai, com a apresentação de um quarto de hotel de nossa autoria.\r\nCom a participação nesta conceituada feira a Ci dá assim mais um passo na sua expansão internacional.', 'We will be present in Dubai, presenting a hotel room of our authorship.\r\nWith the particitpation on this renowed fair, Ci takes another step into its international expansion.', '2017-05-04', '2017-05-04', '/img/noticias/0006.png', 1),
(7, 'INDEX DUBAI 2018', '', 'Pelo segundo ano consecutivo, estaremos presentes na Index. \r\nUma feira dedicada ao design que decorre no Dubai World Trade Centre.', '', '2018-03-13', '2018-03-27', '/img/noticias/0007.png', 1);
